SET NOCOUNT ON;
IF DB_ID(N'PTUD') IS NULL CREATE DATABASE [PTUD];
USE [PTUD];
GO

IF OBJECT_ID('dbo.Exercises','U') IS NOT NULL DROP TABLE dbo.Exercises;
IF OBJECT_ID('dbo.Forms','U') IS NOT NULL DROP TABLE dbo.Forms;
IF OBJECT_ID('dbo.Subjects','U') IS NOT NULL DROP TABLE dbo.Subjects;


CREATE TABLE dbo.Subjects (
  subject_id NVARCHAR(100) PRIMARY KEY,
  subject_name NVARCHAR(255),
  description NVARCHAR(MAX),
  total_exercises INT
);

CREATE TABLE dbo.Forms (
  form_id NVARCHAR(100),
  subject_id NVARCHAR(100) REFERENCES dbo.Subjects(subject_id),
  name NVARCHAR(255),
  difficulty NVARCHAR(50),
  exercise_count INT,
  PRIMARY KEY (subject_id, form_id)
);

CREATE TABLE dbo.Exercises (
  id NVARCHAR(200) PRIMARY KEY,
  subject_id NVARCHAR(100) REFERENCES dbo.Subjects(subject_id),
  form_id NVARCHAR(100),
  title NVARCHAR(400),
  difficulty NVARCHAR(50),
  description NVARCHAR(MAX),
  requirements NVARCHAR(MAX),
  grading_criteria NVARCHAR(MAX),
  attached_files NVARCHAR(MAX),
  submission_format NVARCHAR(200),
  example_input NVARCHAR(MAX),
  example_output NVARCHAR(MAX),
  grading_total INT,
  grading_summary NVARCHAR(MAX)
);

GO


PRINT N'Migration script generated.';
