INSERT INTO dbo.Exercises (id, subject_id, form_id, title, difficulty, description, requirements, grading_criteria, attached_files, submission_format, example_input, example_output, grading_total, grading_summary) VALUES (N'CS202-3-1', N'CS101', N'1', N'[Khó] Bài 1: Thiết kế hệ thống quản lý sinh viên sử dụng OOP', N'Dễ', N'Viết chương trình thiết kế hệ thống quản lý sinh viên gồm các lớp: SinhVien, LopHoc, QuanLySinhVien, hỗ trợ các chức năng thêm, sửa, xóa, tìm kiếm sinh viên.', N'["Định nghĩa đầy đủ các lớp và mối quan hệ kế thừa, đa hình.","Cài đặt các chức năng thêm, sửa, xóa, tìm kiếm sinh viên.","Kiểm tra dữ liệu đầu vào hợp lệ.","Chú thích mã nguồn rõ ràng."]', N'[{"name":"Core correctness (logic)","points":10,"note":"Đúng và đầy đủ yêu cầu đề bài"},{"name":"Input validation & error handling","points":3,"note":"Xử lý các trường hợp vào ra phức tạp"},{"name":"Edge cases & robustness","points":2,"note":"Các trường hợp biên phức tạp"},{"name":"Design & architecture (modularity)","points":3,"note":"Thiết kế mô-đun, nguyên lý tốt"},{"name":"Code quality & documentation","points":2,"note":"Đặt tên, cấu trúc, tài liệu tốt"}]', N'[]', N'*.cpp hoặc *.py', N'', N'', 20, N'Core correctness (logic): | Input validation & error handling: | Edge cases & robustness: | Design & architecture (modularity): | Code quality & documentation:');
