INSERT INTO dbo.Exercises (id, subject_id, form_id, title, difficulty, description, requirements, grading_criteria, attached_files, submission_format, example_input, example_output, grading_total, grading_summary) VALUES (N'CS102-3-8', N'CS201', N'1', N'[Khó] Bài 8: Viết hàm tìm số xuất hiện nhiều nhất trong hai mảng hợp nhất', N'Dễ', N'Viết hàm nhận vào hai mảng số nguyên, hợp nhất hai mảng và tìm số xuất hiện nhiều nhất trong mảng hợp nhất.', N'["Định nghĩa hàm đúng cú pháp.","Hợp nhất hai mảng và tìm số xuất hiện nhiều nhất bằng thuật toán tối ưu.","Trả về kết quả đúng.","Chú thích mã nguồn rõ ràng."]', N'[{"name":"Core correctness (logic)","points":10,"note":"Hoàn toàn đúng cho tập test mở rộng"},{"name":"Input validation & error handling","points":2,"note":"Xử lý mọi trường hợp đầu vào không hợp lệ"},{"name":"Edge cases & robustness","points":2,"note":"Các trường hợp biên phức tạp"},{"name":"Algorithmic efficiency & scalability","points":4,"note":"Độ phức tạp phù hợp và tối ưu cho dữ liệu lớn"},{"name":"Design & architecture (modularity)","points":1,"note":"Thiết kế rõ ràng, dễ mở rộng"},{"name":"Code quality & documentation","points":1,"note":"Mã sạch, có tài liệu/nhận xét hợp lý"}]', N'[]', N'*.c hoặc *.cpp hoặc *.py', N'', N'', 20, N'Core correctness (logic): | Input validation & error handling: | Edge cases & robustness: | Algorithmic efficiency & scalability: | Design & architecture (modularity): | Code quality & documentation:');
